// Μουσεῖον τῆς Ἀτμός
import React from 'react'
import { PlayIcon } from '@heroicons/react/solid'

export default class extends React.Component {
  state = {
    playing: false,
    audio: new Audio('https://picardia.co/mouseion/songs/f93bc6ed21df56f05d3f013eeff74b3da69f16f0.mp3')
  }

  togglePlay = () => {
    this.setState({ playing: !this.state.playing }, () => {
      if (this.state.playing) {
        this.audio.play()
      } else {
        this.audio.src = 'https://picardia.co/mouseion/songs/240e9efbedf78b2258e74987d4d0b0ff27d9fe14.mp3'
        // this.audio.pause()
      }
    })
  }

  render () {
    return (<>
      <div class="bg-red-500 fixed inset-x-0 top-0 p-4 grid grid-cols-3">
        <h1 class="self-center">Μουσεῖον τῆς Ἀτμός</h1>
        <input class="justify-self-stretch h-10 rounded-md py-2 px-3"></input>
        <img></img>
      </div>
      <div class="bg-blue-500 fixed inset-x-0 bottom-0 p-2 grid grid-cols-3">
        <div class="col-span-3 bg-purple-500"></div>
        <div></div>
        <div class="bg-green-500 justify-self-center">
          <PlayIcon class="h-10"></PlayIcon>
        </div>
        <div></div>
      </div>
    </>)
  }
}
