/* eslint-env browser */
import { useState, useEffect, useRef } from 'react'
import { Switch, Route } from 'react-router-dom'

import { artistsCol } from './firestore'

function Thumbnail (props) {
  const image = useRef()

  useEffect(() => {
    const observer = new IntersectionObserver((entities) => {
      if (entities[0].isIntersecting) {
        image.current.src = 'https://picardia.co/static/papes/4b50dbaa-624e-11e7-b2fe-3a6e8f7c670f.jpg'
        image.current.addEventListener('load', () => {
          image.current.style.opacity = 1
        })
        observer.disconnect()
      }
    })
    observer.observe(image.current)
  }, [])

  return (
    <div className='m-2 transition bg-gray-800 bg-opacity-60 hover:filter brightness-150 p-2 hover:bg-gray-400 hover:text-gray-800'>
      <img className='w-40 h-40 object-cover object-center opacity-0 transition-opacity delay-1000' ref={image} />
      <p className='max-w-40 text-xl text-center truncate'>{props.text}</p>
    </div>
  )
}

function Home () {
  return <h1>Home</h1>
}

function Artists () {
  const [list, setList] = useState([])
  const [page, setPage] = useState(0)

  useEffect(async () => {
    const query = await artistsCol.orderBy('name').limit(100).get()
    setList(query.docs)
  }, [])

  return (
    <>
      <h1 className='text-3xl pb-4 border-b border-gray-600 border-opacity-70'>Artists</h1>
      <div className='flex flex-wrap'>
        {list.map((v, i) => {
          return <Thumbnail key={i} text={v.data().name} />
        })}
      </div>
    </>
  )
}

export default function MainContent () {
  return (
    <div className='bg-main py-16 lg:py-20 min-h-screen'>
      <div className='mx-2 sm:mx-4 md:mx-8 lg:mx-16 xl:mx-30 2xl:mx-40 space-y-4'>
        <Switch>
          <Route exact path='/'>
            <Home />
          </Route>
          <Route exact path='/labels'>
            <Home />
          </Route>
          <Route exact path='/artists'>
            <Artists />
          </Route>
          <Route exact path='/albums'>
            <Home />
          </Route>
          <Route exact path='/songs'>
            <Home />
          </Route>
          <Route exact path='/search'>
            <Home />
          </Route>
          <Route exact path='/settings'>
            <Home />
          </Route>
        </Switch>
      </div>
    </div>
  )
}
