// Μουσεῖον τῆς Ἀτμός
import React from 'react'

export default class extends React.Component {
  constructor (props) {
    super(props)
    this.audio = new Audio('https://picardia.co/mouseion/songs/f93bc6ed21df56f05d3f013eeff74b3da69f16f0.mp3')
  }
  state = {
    playing: false
  }

  togglePlay = () => {
    this.setState({ playing: !this.state.playing }, () => {
      if (this.state.playing) {
        this.audio.play()
      } else {
        this.audio.src = 'https://picardia.co/mouseion/songs/240e9efbedf78b2258e74987d4d0b0ff27d9fe14.mp3'
        // this.audio.pause()
      }
    })
  }

  render () {
    return (
      <div>
        <div class="lg:fixed">

        </div>
      </div>
    )
  }
}
