import React from 'react'

class TimeStat extends React.Component {
  render () {
    const cur = this.props.audio.currentTime
    let dur = this.props.audio.duration
    if (isNaN(dur)) dur = 0
    const ch = String(Math.floor(cur / 3600))
    let cm = String(Math.floor(cur / 60) % 60)
    if (dur >= 600) cm = cm.padStart(2, 0)
    const cs = String(Math.floor(cur) % 60).padStart(2, 0)
    const dh = String(Math.floor(dur / 3600))
    const dm = String(Math.floor(dur / 60) % 60)
    if (dur >= 600) dm = dm.padStart(2, 0)
    const ds = String(Math.floor(dur) % 60).padStart(2, 0)
    let curStr = `${cm}:${cs}`
    let durStr = `${dm}:${ds}`
    if (dur >= 3600) {
      curStr = `${ch}:` + curStr
      durStr = `${dh}:` + durStr
    }
    const string = `${curStr} / ${durStr}`
    return <span class="hidden mx-2 text-xs tracking-tighter md:block">{string}</span>
  }
}

export default class extends React.Component {
  constructor (props) {
    super(props)
    this.togglePause = this.togglePause.bind(this)
  }

  togglePause () {
    if (this.props.audio.paused) {
      this.props.audio.play()
    } else {
      this.props.audio.pause()
    }
  }

  changeVol (event) {
    console.log(event.target.value)
  }

  render () { return (
    <div class="bg-red-400 fixed inset-x-0 bottom-0 grid grid-cols-3">
      <div>
        <div class="flex items-center">
          <div class="p-2 ml-2">
            <svg class="cursor-pointer h-6" viewBox="0 0 24 24" focusable="false"><g><path d="M6 6h2v12H6zm3.5 6l8.5 6V6z"/></g></svg>
          </div>
          <div class="p-2 md:ml-2" onClick={this.togglePause}>
            <svg class="cursor-pointer h-8 sm:h-10" viewBox="0 0 24 24" focusable="false"><g><path d={!this.props.audio.paused ? 'M6 19h4V5H6v14zm8-14v14h4V5h-4z' : 'M8 5v14l11-7z'}/></g></svg>
          </div>
          <div class="p-2 md:ml-2">
          <svg class="cursor-pointer h-6" viewBox="0 0 24 24" focusable="false"><g><path d="M6 18l8.5-6L6 6v12zM16 6v12h2V6h-2z"/></g></svg>
          </div>
          <TimeStat audio={this.props.audio}/>
        </div>
      </div>
      <div class="flex items-center">
        <img class="h-10 w-10" src="https://picardia.co/static/papes/1588295138916.jpg"/>
        <div class="mx-4 text-sm overflow-hidden">
          <p class="truncate"><b>Song Name</b></p>
          <p class="truncate">Artist Name â€¢ Album Name</p>
        </div>
      </div>
      <div class="flex items-center">
        <input class="h-1 w-20" type="range" min="0" max="1000" onChange={this.changeVol}></input>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
      </div>
    </div>
  ) }
}
