import { useState, useEffect, useRef } from 'react'

import firebase from 'firebase/app'
import 'firebase/firestore'

firebase.initializeApp({
  projectId: 'mouseion-tis-atmos'
})

const firestore = firebase.firestore()

function ArtistEntry (props) {
  const [artist, setArtist] = useState(props.artist.data())
  const [ref, setRef] = useState(null)
  return <p onClick={() => props.clickHandler(props.artist)}>{artist.name}</p>
}

function ArtistView (props) {
  if (props.currentArtist == null) return null
}

export default function MainContent () {
  const [mode, setMode] = useState('artists')
  const [artistList, setArtistList] = useState([])
  const [currentArtist, setCurrentArtist] = useState(null)

  useEffect(async () => {
    const list = await firestore.collection('t2artists').orderBy('name').limit(10).get()
    setArtistList(list.docs)
  }, [])

  return (
    <div className="bg-main py-12 md:py-20 text-white min-h-screen">
      <div className="mx-2 sm:mx-4 md:mx-8 lg:mx-16 xl:mx-30 2xl:mx-40 space-y-4">
        <div className={mode === 'artists' ? '' : 'hidden'}>
          <h1 className="text-xl">All Artists</h1>
          {artistList.map((artist, index) => <ArtistEntry
              key={index}
              artist={artist}
              clickHandler={(artist) => {
                setMode('artist')
                setCurrentArtist(artist)
              }}
          />)}
        </div>
        <ArtistView
          currentArtist={currentArtist}
        />
      </div>
    </div>
  )
}
