import { useState, useEffect, useRef } from 'react'

import firebase from 'firebase/app'
import 'firebase/firestore'

firebase.initializeApp({
  projectId: 'mouseion-tis-atmos'
})

const firestore = firebase.firestore()

export default function () {
  const [itemList, setItemList] = useState([])
  const [page, setPage] = useState(0)
  const [loading, setLoading] = useState(true)
  const loader = useRef()
  let observer = null

  useEffect(() => {
    console.log('Element initialised')
    const observer = new IntersectionObserver(async entities => {
      for (let entity of entities) {
        console.log('Loader seen.')
        setPage(page + 1)
      }
    })
    observer.observe(loader.current)
    console.log(observer)
  }, [])

  useEffect(async () => {
    console.log('Page changed', page)
    console.log('Starting fake loading')
    await new Promise((res, rej) => {
      setTimeout(res, 2000)
    })
    console.log('Fake loading finished')
  }, [page])

  return (
    <div class="bg-main py-12 md:py-20 text-white min-h-screen">
      <div class="mx-2 sm:mx-4 md:mx-8 lg:mx-16 xl:mx-30 2xl:mx-40">
        {
          itemList.map((v, i) => (
            <p class="text-xl" key={i}>{v}</p>
          ))
        }
        {loading ? <p class="text-xl" ref={loader}>Loading ...</p> : null}
      </div>
    </div>
  )
}
