/* eslint-env browser */

import { useEffect, useState, useRef } from 'react'

function Thumbnail (props) {
  const image = useRef()

  useEffect(() => {
    const observer = new IntersectionObserver((entities) => {
      console.log('Observer event. Intersecting:', entities[0].isIntersecting)
      if (entities[0].isIntersecting) {
        image.current.src = 'https://picardia.co/static/papes/4b50dbaa-624e-11e7-b2fe-3a6e8f7c670f.jpg'
        if (Math.random() > 0.5) image.current.src = 'https://www.sophielaura.co.uk/wp-content/uploads/2020/04/Blossom-Phone-Wallpaper-Background-Sophie-Laura-Blog-1-scaled.jpg'
        image.current.addEventListener('load', () => {
          image.current.style.opacity = 1
        })
        observer.disconnect()
      }
    })
    observer.observe(image.current)
  }, [])

  return (
    <div className='m-4 bg-gray-800 bg-opacity-60 hover:filter brightness-150'>
      <div>
        <img className='h-64 w-64 object-cover object-center opacity-0 transition-opacity' ref={image} />
      </div>
      <p className='w-60 my-2 mx-auto text-xl text-center truncate'>{props.text}</p>
    </div>
  )
}

export default function MainContent () {
  const [mode, setMode] = useState('landing')

  return (
    <div className='bg-main py-12 md:py-20 text-white min-h-screen'>
      <div className='mx-2 sm:mx-4 md:mx-8 lg:mx-16 xl:mx-30 2xl:mx-40 space-y-4'>
        {{
          landing: (
            <>
              <h1 class='mx-4 pb-2 text-4xl border-b'>Mouseion tis Atmos</h1>
              <div className='flex justify-between'>
                <Thumbnail text='Labels aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa' />
                <Thumbnail text='Artists' />
                <Thumbnail text='Albums' />
                <Thumbnail text='Songs' />
              </div>
            </>
          ),
          artists: (
            <div>
              <p>hi</p>
            </div>
          )
        }[mode]}
      </div>
    </div>
  )
}
