// Μουσεῖον τῆς Ἀτμός
import React from 'react'
import {
  ChevronDoubleLeftIcon,
  ChevronDoubleRightIcon,
  PauseIcon,
  PlayIcon
} from '@heroicons/react/solid'

export default class extends React.Component {
  state = {
    playing: false,
    //audio: new Audio('https://picardia.co/mouseion/songs/f93bc6ed21df56f05d3f013eeff74b3da69f16f0.mp3')
  }

  togglePlay = () => {
    this.setState({ playing: !this.state.playing }, () => {
      if (this.state.playing) {
        this.audio.play()
      } else {
        // this.audio.src = 'https://picardia.co/mouseion/songs/240e9efbedf78b2258e74987d4d0b0ff27d9fe14.mp3'
        this.audio.pause()
      }
    })
  }

  render () {
    return (<>
      <div class="bg-red-500 fixed inset-x-0 top-0 p-4 grid grid-cols-3 items-center">
        <h1 class="hidden sm:block">Header title</h1>
        <input class="justify-self-stretch h-10 rounded-md py-2 px-3" placeholder="Search"></input>
        <img></img>
      </div>
      <div class="bg-blue-900 fixed inset-x-0 bottom-0">
        <div class="col-span-3 bg-blue-700 h-2 hover:"></div>
        <div class="bg-pink-400">
          <img class="h-14 w-14" src="https://picardia.co/static/papes/1585764246581.jpg"></img>
          <p>Song name</p>
          <p><u>Artist Name</u> • <u>Album Name</u></p>
        </div>
        <div class="bg-green-500 justify-self-center h-18 flex items-center">
          <ChevronDoubleLeftIcon class="h-14 text-white"/>
          <PlayIcon class="h-16 text-white"/>
          <ChevronDoubleRightIcon class="h-14 text-white"/>
        </div>
        <div class="bg-yellow-400"></div>
      </div>
    </>)
  }
}
