import React from 'react'
import ReactDOM from 'react-dom'
import './style.scss'
import BottomPlayer from './bottom-player'
import {
  QueueIcon,
  PauseIcon,
  PlayIcon,
  RepeatIcon,
  Repeat1Icon,
  SettingsIcon,
  ShuffleIcon,
  StepBackIcon,
  StepFwdIcon,
  VolumeOffIcon,
  VolumeLowIcon,
  VolumeMedIcon,
  VolumeHighIcon,
} from './icons'

class Root extends React.Component {
  constructor (props) {
    super(props)
    const audio = new Audio()
    audio.src = 'https://picardia.co/mouseion/songs/f3b7681372e8f95117aef9388590a3942f0bc64d.mp3'
    audio.addEventListener('timeupdate', () => {
      this.setState({ currentTime: audio.currentTime })
    })
    audio.addEventListener('durationchange', () => {
      this.setState({ duration: audio.duration })
    })
    this.state = {
      audio: audio,
      currentTime: 0,
      duration: 0,
      volume: 1,
      oldVolume: 1,
      paused: true
    }
  }

  changeVol = (event) => {
    const newVol = Number(event.target.value) / 1000
    this.setState({ volume: newVol }, () => {
      this.state.audio.volume = newVol
    })
  }

  toggleMute = () => {
    if (this.state.audio.volume === 0) {
      this.state.audio.volume = this.state.oldVolume
    } else {
      this.setState({ oldVolume: this.props.audio.volume }, () => {
        this.props.audio.volume = 0
      })
    }
  }

  togglePause = () => {
    this.setState({ paused: !this.state.paused }, () => {
      if (this.state.audio.paused) {
        this.state.audio.play()
      } else {
        this.state.audio.pause()
      }
    })
  }

  render () { return (<>
    <Navbar/>
    <BottomPlayer
      currentTime={this.state.currentTime}
      duration={this.state.duration}
      volume={this.state.volume}
      changeVol={this.changeVol}
      toggleMute={this.toggleMute}
      paused={this.state.paused}
      togglePause={this.togglePause}
    />
    <MainContent/>
  </>) }
}

class Navbar extends React.Component {
  render () { return (<div>
    <p>Base</p>
    <p class="hidden sm:block">Small</p>
    <p class="hidden md:block">Medium</p>
    <p class="hidden lg:block">Large</p>
    <p class="hidden xl:block">X Large</p>
    <p class="hidden 2xl:block">2x Large</p>
  </div>) }
}

class MainContent extends React.Component {
  render () { return null }
}

ReactDOM.render(
  <React.StrictMode>
    <Root/>
  </React.StrictMode>,
  document.body
)
