import React from 'react'
import ReactDOM from 'react-dom'
import './style.scss'
import BottomPlayer from './bottom-player'

class Root extends React.Component {
  constructor (props) {
    super(props)
    this.audio = new Audio()
    this.audio.src = 'https://picardia.co/mouseion/songs/f3b7681372e8f95117aef9388590a3942f0bc64d.mp3'
    this.audio.addEventListener('timeupdate', () => {
      this.setState({ currentTime: this.audio.currentTime })
    })
    this.audio.addEventListener('durationchange', () => {
      this.setState({ duration: this.audio.duration })
    })
    this.state = {
      currentTime: 0,
      duration: 0,
      volume: 1,
      oldVolume: 1,
      loop: 0,
      shuffle: false,
      paused: true
    }
  }

  changeVol = (event) => {
    const newVol = Number(event.target.value) / 1000
    this.setState({ volume: newVol, oldVolume: newVol }, () => {
      this.audio.volume = newVol
    })
  }

  toggleMute = () => {
    if (this.state.volume !== 0) {
      this.setState({ volume: 0, oldVolume: this.state.volume }, () => {
        this.audio.volume = 0
      })
    } else {
      this.setState({ volume: this.state.oldVolume }, () => {
        this.audio.volume = this.state.oldVolume
      })
    }
  }

  toggleLoop = () => {
    this.setState({ loop: (this.state.loop + 1) % 3 })
  }

  toggleShuffle = () => {
    this.setState({ shuffle: !this.state.shuffle })
  }

  togglePause = () => {
    this.setState({ paused: !this.state.paused }, () => {
      if (this.audio.paused) {
        this.audio.play()
      } else {
        this.audio.pause()
      }
    })
  }

  render () { return (<>
    <Navbar/>
    <BottomPlayer
      currentTime={this.state.currentTime}
      duration={this.state.duration}
      volume={this.state.volume}
      changeVol={this.changeVol}
      toggleMute={this.toggleMute}
      loop={this.state.loop}
      toggleLoop={this.toggleLoop}
      shuffle={this.state.shuffle}
      toggleShuffle={this.toggleShuffle}
      paused={this.state.paused}
      togglePause={this.togglePause}
    />
    <MainContent/>
  </>) }
}

class Navbar extends React.Component {
  render () { return (<div>
    <p>Base</p>
    <p class="hidden sm:block">Small</p>
    <p class="hidden md:block">Medium</p>
    <p class="hidden lg:block">Large</p>
    <p class="hidden xl:block">X Large</p>
    <p class="hidden 2xl:block">2x Large</p>
  </div>) }
}

class MainContent extends React.Component {
  render () { return null }
}

ReactDOM.render(
  <React.StrictMode>
    <Root/>
  </React.StrictMode>,
  document.body
)
