import React from 'react'
import ReactDOM from 'react-dom'
import './style.css'
import BottomPlayer from './bottom-player'

class Root extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      audio: new Audio('https://picardia.co/mouseion/songs/f3b7681372e8f95117aef9388590a3942f0bc64d.mp3')
    }
    this.state.audio.addEventListener('timeupdate', () => {
      this.setState({})
    })
  }

  handleAudio (event) {
    console.log(this)
  }

  render () { return (<>
    <Navbar/>
    <audio controls src="https://picardia.co/mouseion/songs/f3b7681372e8f95117aef9388590a3942f0bc64d.mp3" onTimeUpdate={this.handleAudio}/>
    <BottomPlayer audio={this.state.audio}/>
    <MainContent/>
  </>) }
}

class Navbar extends React.Component {
  render () { return null }
}

class MainContent extends React.Component {
  render () { return null }
}

ReactDOM.render(
  <React.StrictMode>
    <Root/>
  </React.StrictMode>,
  document.body
)
