import React, { useEffect, useRef } from 'react'
import { useState } from 'react'
import ReactDOM from 'react-dom'

import './style.scss'

import Navbar from './navbar'
// import BottomPlayer from './bottom-player'
import BottomPlayer from './bottom-player'
import MainContent from './main-content'
// https://picardia.co/static/papes/4b50dbca-624e-11e7-b2fe-3a6e8f7c670f.jpg
class Root extends React.Component {
  constructor (props) {
    super(props)
    this.audio = new Audio()
    this.audio.src = 'https://picardia.co/mouseion/songs/f3b7681372e8f95117aef9388590a3942f0bc64d.mp3'
    this.audio.addEventListener('timeupdate', () => {
      this.setState({ currentTime: this.audio.currentTime })
    })
    this.audio.addEventListener('durationchange', () => {
      this.setState({ duration: this.audio.duration })
    })
    this.state = {
      currentTime: 0,
      duration: 0,
      volume: 1,
      oldVolume: 1,
      loop: 0,
      shuffle: false,
      paused: true
    }
  }

  changeVol = (event) => {
    const newVol = Number(event.target.value) / 1000
    this.setState({ volume: newVol, oldVolume: newVol }, () => {
      this.audio.volume = newVol
    })
  }

  toggleMute = () => {
    if (this.state.volume !== 0) {
      this.setState({ volume: 0, oldVolume: this.state.volume }, () => {
        this.audio.volume = 0
      })
    } else {
      this.setState({ volume: this.state.oldVolume }, () => {
        this.audio.volume = this.state.oldVolume
      })
    }
  }

  toggleLoop = () => {
    this.setState({ loop: (this.state.loop + 1) % 3 })
  }

  toggleShuffle = () => {
    this.setState({ shuffle: !this.state.shuffle })
  }

  togglePause = () => {
    this.setState({ paused: !this.state.paused }, () => {
      if (this.audio.paused) {
        this.audio.play()
      } else {
        this.audio.pause()
      }
    })
  }

  seek = (event) => {
    const newTime = (Number(event.target.value) / 1000) * this.audio.duration
    this.audio.currentTime = newTime
  }

  render () { return (<>
    <Navbar/>
    <BottomPlayer2
      currentTime={this.state.currentTime}
      duration={this.state.duration}
      volume={this.state.volume}
      changeVol={this.changeVol}
      toggleMute={this.toggleMute}
      loop={this.state.loop}
      toggleLoop={this.toggleLoop}
      shuffle={this.state.shuffle}
      toggleShuffle={this.toggleShuffle}
      paused={this.state.paused}
      togglePause={this.togglePause}
      seek={this.seek}
    />
    <MainContent/>
  </>) }
}

function Root2 () {
  const [currentTime, setCurrentTime] = useState(0)
  const [duration, setDuration] = useState(0)
  const [volume, setVolume] = useState(1)
  const [oldVolume, setOldVolume] = useState(1)
  const [repeat, setRepeat] = useState(0)
  const [shuffle, setShuffle] = useState(false)
  const [paused, setPaused] = useState(true)
  const audio = useRef(null)

  useEffect(() => {
    audio.current.src = 'https://picardia.co/mouseion/songs/f3b7681372e8f95117aef9388590a3942f0bc64d.mp3'
    audio.current.addEventListener('timeupdate', () => {
      setCurrentTime(audio.current.currentTime)
    })
    audio.current.addEventListener('durationchange', () => {
      setDuration(audio.current.duration)
    })
  }, [])

  function changeVol (newVol) {
    audio.current.volume = newVol
    setVolume(newVol)
    setOldVolume(newVol)
  }
  function toggleMute () {
    if (volume !== 0) {
      audio.current.volume = 0
      setOldVolume(volume)
      setVolume(0)
    } else {
      audio.current.volume = oldVolume
      setVolume(oldVolume)
    }
  }
  function changeRepeat () {
    setRepeat((repeat + 1) % 3)
  }
  function toggleShuffle () {
    setShuffle(!shuffle)
  }
  function togglePause () {
    if (paused) {
      audio.current.play()
    } else {
      audio.current.pause()
    }
    setPaused(!paused)
  }
  function seek (timeFrac) {
    const newTime = timeFrac * duration
    audio.current.currentTime = newTime
  }

  return (<>
    <audio ref={audio}/>
    <Navbar/>
    <BottomPlayer
      currentTime={currentTime}
      duration={duration}
      volume={volume}
      repeat={repeat}
      shuffle={shuffle}
      paused={paused}
      handleChangeVol={changeVol}
      handleToggleMute={toggleMute}
      handleChangeRepeat={changeRepeat}
      handleToggleShuffle={toggleShuffle}
      handleTogglePause={togglePause}
      handleSeek={seek}
    />
    <MainContent/>
  </>)
}

ReactDOM.render(
  <React.StrictMode>
    <Root2/>
  </React.StrictMode>,
  document.getElementById('root')
)
