import React from 'react'

import {
  PauseIcon,
  PlayIcon,
  QueueIcon,
  RepeatIcon,
  Repeat1Icon,
  SettingsIcon,
  ShuffleIcon,
  StepBackIcon,
  StepFwdIcon,
  VolumeOffIcon,
  VolumeLowIcon,
  VolumeMedIcon,
  VolumeHighIcon,
} from './icons'

class TimeStat extends React.Component {
  render () {
    const cur = this.props.audio.currentTime
    let dur = this.props.audio.duration
    if (isNaN(dur)) dur = 0
    const ch = String(Math.floor(cur / 3600))
    let cm = String(Math.floor(cur / 60) % 60)
    if (dur >= 600) cm = cm.padStart(2, 0)
    const cs = String(Math.floor(cur) % 60).padStart(2, 0)
    const dh = String(Math.floor(dur / 3600))
    const dm = String(Math.floor(dur / 60) % 60)
    if (dur >= 600) dm = dm.padStart(2, 0)
    const ds = String(Math.floor(dur) % 60).padStart(2, 0)
    let curStr = `${cm}:${cs}`
    let durStr = `${dm}:${ds}`
    if (dur >= 3600) {
      curStr = `${ch}:` + curStr
      durStr = `${dh}:` + durStr
    }
    const string = `${curStr} / ${durStr}`
    return <span class={this.props.class}>{string}</span>
  }
}

export default class extends React.Component {
  state = {
    oldVolume: this.props.audio.volume
  }

  toggleMute = () => {
    if (this.props.audio.volume === 0) {
      this.props.audio.volume = this.state.oldVolume
    } else {
      this.setState({ oldVolume: this.props.audio.volume }, () => {
        this.props.audio.volume = 0
      })
    }
  }

  togglePause = () => {
    if (this.props.audio.paused) {
      this.props.audio.play()
    } else {
      this.props.audio.pause()
    }
  }

  changeVol = (event) => {
    const newVol = Number(event.target.value) / 1000
    this.props.audio.volume = newVol
  }

  render () { return (
    <div class="bg-red-400 fixed inset-x-0 bottom-0 grid grid-cols-2">
      <div class="flex items-center space-x-2 sm:mx-2 md:space-x-4">
        <img class="h-10 w-10 hidden md:block" src="https://picardia.co/static/papes/1588295138916.jpg"/>
        <div class="text-sm overflow-hidden">
          <p class="truncate cursor-pointer hover:underline select-none"><b>eeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeee</b></p>
          <p class="truncate select-none"><span class="cursor-pointer hover:underline">{'aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa'}</span> • <span class="cursor-pointer hover:underline">{'cccccccccccccccccccccccccccccccccccccccccccccccccccccccccccccccccccccccccc'}</span></p>
        </div>
        <TimeStat class="whitespace-nowrap text-xs tracking-tighter hidden sm:block" audio={this.props.audio}/>
      </div>
      <div class="flex items-center justify-end bg-blue-400 lg:space-x-2">
        <input class="h-1 w-20 hidden sm:block" type="range" min="0" max="1000" defaultValue={this.props.audio.volume * 1000} onChange={this.changeVol}></input>
        <div class="cursor-pointer p-1 md:p-2">{
          this.props.audio.volume === 0
          ? <VolumeOffIcon class="h-6"/>
          : this.props.audio.volume < 1/3
          ? <VolumeLowIcon class="h-6"/>
          : this.props.audio.volume < 2/3
          ? <VolumeMedIcon class="h-6"/>
          : <VolumeHighIcon class="h-6"/>
        }</div>
        <div class="cursor-pointer hidden sm:block p-1 md:p-2">
          <RepeatIcon class="h-6"/>
        </div>
        <div class="cursor-pointer hidden sm:block p-1 md:p-2">
          <ShuffleIcon class="h-6"/>
        </div>
        <div class="cursor-pointer hidden sm:block p-1 md:p-2">
          <QueueIcon class="h-6"/>
        </div>
        <div class="cursor-pointer hidden sm:block p-1 md:p-2">
          <StepBackIcon class="h-6"/>
        </div>
        <div class="cursor-pointer p-1 md:p-2" onClick={this.togglePause}>{
          this.props.audio.paused
          ? <PlayIcon class="h-8 md:h-10"/>
          : <PauseIcon class="h-8 md:h-10"/>
        }</div>
        <div class="cursor-pointer p-1 md:p-2">
          <StepFwdIcon class="h-6"/>
        </div>
        <div class="cursor-pointer sm:hidden p-1 md:p-2">
          <StepFwdIcon class="h-6"/>
        </div>
      </div>
    </div>
  ) }
}
